postprocessing.action:
set search_path=hisinone; update course_of_study set astat_degree = SUBSTRING(uniquename,0,3) where astat_degree is null and uniquename IS NOT NULL;
update hisinone.course_of_study set astat_subject = helper.src_astat_subject from (
select distinct on (dst.uniquename) regexp_replace(src.uniquename, '^..\|(...)\|.+\|$','\1') stg, src.uniquename src_uniquename, src.astat_subject src_astat_subject, dst.uniquename dst_uniquename, dst.astat_subject dst_astat_subject
from hisinone.course_of_study src join hisinone.course_of_study dst on ( src.uniquename is not null and dst.uniquename is not null and regexp_replace(src.uniquename, '^..\|(...)\|.+\|$','\1') = regexp_replace(dst.uniquename, '^..\|(...)\|.+\|$','\1') ) where src.uniquename != dst.uniquename and dst.astat_subject is null and src.astat_subject is not null
) as helper
where uniquename = helper.dst_uniquename and astat_subject is null ;
update hisinone.course_of_study set regular_number_of_semesters = helper.src_regular_number_of_semesters from ( with src as ( select uniquename, regexp_replace(uniquename,'^([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|$', '\1') abschl, regexp_replace(uniquename,'^([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|$', '\2') stg, regexp_replace(uniquename,'^([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|$', '\6') pversion, regular_number_of_semesters from hisinone.course_of_study where uniquename is not null and uniquename not in ('APP', 'Studienangebot') and regular_number_of_semesters is not null ), dst as ( select uniquename, regexp_replace(uniquename,'^([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|$', '\1') abschl, regexp_replace(uniquename,'^([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|$', '\2') stg, regexp_replace(uniquename,'^([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|([^\|]*)\|$', '\6') pversion from hisinone.course_of_study where uniquename is not null and uniquename not in ('APP', 'Studienangebot') and regular_number_of_semesters is null ) select distinct on (dst.uniquename) src.uniquename src_uniquename, dst.uniquename dst_uniquename, abschl, stg, pversion, src.regular_number_of_semesters src_regular_number_of_semesters from src join dst using (abschl, stg, pversion) ) as helper where uniquename = helper.dst_uniquename and regular_number_of_semesters is null returning (uniquename, src_uniquename || ' ---> ' || src_regular_number_of_semesters::varchar) ;

preprocessing.action:
